    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {                                         
        //ekle
    	if(jTextField1.getText().equals("")|| (jTextField2.getText().equals("") || jTextField3.getText().equals(""))){
    		JOptionPane.showMessageDialog(null,"Lütfen ürün bilgilerini giriniz!");
    	}
    	else{
    	Stokelemanlar se = new Stokelemanlar(jTextField1.getText(),jTextField2.getText(),jTextField3.getText(),jTextField4.getText(),jTextField8.getText(),jTextField5.getText(),jTextField9.getText(),jTextField10.getText());
    	stok.UrunEkle(se);
    	
    	}
       }                                        

