    private void jButton6ActionPerformed(java.awt.event.ActionEvent evt) {                                         
    	if(jTextField6.getText().equals("")){
    		JOptionPane.showMessageDialog(null, "Öncelikle işlem seçiniz!");
    	}
    	else{
    	int toplam = list.size();
	       int deger= Integer.parseInt(jTextField6.getText());
	       if (toplam!=0) {
			if (deger!=0) {
				deger--;
				if (deger>0) {
					String degerstr = Integer.toString(deger);
					jTextField6.setText(degerstr);
					
					
					jTextField1.setText(list.get(deger-1).getUrunadi());
					jTextField2.setText(list.get(deger-1).getUrunkodu());
					jTextField3.setText(list.get(deger-1).getUgrsmiktar());
					jTextField4.setText(list.get(deger-1).getUcksmiktar());
					jTextField8.setText(list.get(deger-1).getUrunfiyat());
					jTextField5.setText(list.get(deger-1).getAlistar());
					jTextField9.setText(list.get(deger-1).getSatistar());
					jTextField10.setText(list.get(deger-1).getUruncinsi());
				}
			}
	       }
	     }
    }                                        

    private void jButton7ActionPerformed(java.awt.event.ActionEvent evt) {                                         
        // ileri
    	if(jTextField6.getText().equals("")){
    		JOptionPane.showMessageDialog(null, "Öncelikle işlem seçiniz!");
    	}
    	else{
    	int toplam = list.size();
	       int deger= Integer.parseInt(jTextField6.getText());
	       if (toplam!=0) {
				if (deger<toplam) {
					deger++;
					String degerstr = Integer.toString(deger);
					jTextField6.setText(degerstr);
					jTextField1.setText(list.get(deger-1).getUrunadi());
					jTextField2.setText(list.get(deger-1).getUrunkodu());
					jTextField3.setText(list.get(deger-1).getUgrsmiktar());
					jTextField4.setText(list.get(deger-1).getUcksmiktar());
					jTextField8.setText(list.get(deger-1).getUrunfiyat());
					jTextField5.setText(list.get(deger-1).getAlistar());
					jTextField9.setText(list.get(deger-1).getSatistar());
					jTextField10.setText(list.get(deger-1).getUruncinsi());
				}
			}
    	}
		
    }                                        

    private void jButton5ActionPerformed(java.awt.event.ActionEvent evt) {                                         
        // stokları görüntüle
    	list=stok.StoklariGoruntule();
    	int tümü= list.size();
    	String tümstrng=Integer.toString(tümü);
    	 jTextField7.setText(tümstrng);
    	 
    	int ilk=0;
        String ilkstr=Integer.toString(ilk);
        jTextField6.setText(ilkstr);
        if(tümü!=0){
        	jTextField1.setText(list.get(0).getUrunadi());
        	jTextField2.setText(list.get(0).getUrunkodu());
        	jTextField3.setText(list.get(0).getUgrsmiktar());
        	jTextField4.setText(list.get(0).getUcksmiktar());
        	jTextField8.setText(list.get(0).getUrunfiyat());
        	jTextField5.setText(list.get(0).getAlistar());
        	jTextField6.setText(list.get(0).getSatistar());
        	jTextField10.setText(list.get(0).getUruncinsi());
        	JOptionPane.showMessageDialog(null, "Tüm stoklar görüntülendi");
		}
        else{
        	JOptionPane.showMessageDialog(null, "Stok listesi boş!");
        }
        
    }                                        

    private void jButton4ActionPerformed(java.awt.event.ActionEvent evt) {                                         
        int liste = list.size();
        if(liste!=0){
        	stok.Kayitsil(jTextField2.getText());        	
        }
        else{
        	JOptionPane.showMessageDialog(null,"Silmek istediğiniz ürünü bulunuz!");
        }
    }                                        

